def exit_choice(exit):
    import os
    import sys
    if exit == 1:
        game_exit()
    elif exit == 2:
        os.system('game.py')
        sys.exit()
    elif exit == 3:
        pass
    else:
        f = open("logs", "a+")
        f.write(f'\n UNKNOWN_EXIT')
        f.close()
        sys.exit()
